;(function($) {

    "use strict";

    var defaultOptions = {
        image: {
            folder: null
        }
    };

    /**
     * Constructor.
     *
     * @param {string}      selector
     * @param {Object}      options  The options object.
     *
     * @constructor
     */
    var TinymceLunaEditor = function(selector, options) {
        options.selector = selector;

        tinymce.init(options);

        this.selector = selector;
        this.editor   = $(selector);
        this.options  = $.extend(true, {}, defaultOptions, options);

        var id = this.editor.attr('id');

        Luna.Editor.editors[selector] = {
            insert: function(text) {
                return tinymce.get(id).insertContent(text);
            },
            getValue: function() {
                return tinymce.get(id).getContent();
            },
            setValue: function(text) {
                return tinymce.get(id).setContent(text);
            }
        };
    };

    // Static methods start
    // ---------------------------------------------------
    TinymceLunaEditor.instances = {};

    /**
     * Get or create an instance.
     *
     * @param {string} selector
     * @param {Object} options
     *
     * @returns {TinymceLunaEditor}
     */
    TinymceLunaEditor.getInstance = function(selector, options) {
        if (!this.instances[selector]) {
            this.instances[selector] = new TinymceLunaEditor(selector, options);
        }

        return this.instances[selector];
    };
    // ---------------------------------------------------
    // Static methods end

    TinymceLunaEditor.prototype = {
        /**
         * Send file to server.
         *
         * @param {File} file
         */
        sendFile: function(file) {
            var data = new FormData;
            var folder = this.options.image.folder || '';
            data.append("file", file);
            data.append('folder', folder);

            var self = this;

            $.ajax({
                data: data,
                type: "POST",
                url: this.options.image_upload_url,
                cache: false,
                contentType: false,
                processData: false,
                success: function(response) {
                    if (response.success) {
                        self.editor.summernote("insertImage", response.data.url);
                    } else {
                        alert('Image upload fail!!!');
                    }
                },
                error: function(error) {
                    console.log(error.responseJSON.message);
                }
            });
        }
    };

    // Push to window
    window.TinymceLunaEditor = TinymceLunaEditor;

})(jQuery);
